function decl_calcUsage(r,id1,id2) {
	target.setValue( Number(r.getSource(id1).getValue()) + (-1 * Number(r.getSource(id2).getValue()) ) );
}

function decl_min(r) {
	var ret = Number(2147483647);
	for( i = 0; i < r.getSources().size(); i++ ) {
		if( r.getSources().get(i).getValue() < ret ) {
			ret = r.getSources().get(i).getValue();
		}
	}
	target.setValue(ret);
}

function decl_max(r) {
	var ret = Number(0);
	for( i = 0; i < r.getSources().size(); i++ ) {
		if( r.getSources().get(i).getValue() > ret ) {
			ret = r.getSources().get(i).getValue();
		}
	}
	target.setValue(ret);
}

function decl_calcNormalizedUsage(r,id,max_id) {
	target.setValue( 
		Number( ((Number(r.getSource(id).getValue()) * 100) / Number(r.getSource(max_id).getValue())) / 100 )
	);
}

function decl_calcTrendingFactor(r,lic_id,factor_id,licenses_id) {
	var total = Number(r.getSource(licenses_id).getValue());
	var lic_count = Number(r.getSource(lic_id).getValue());
	var factor = Number(r.getSource(factor_id).getValue());
	var s = Number( ( factor * lic_count ) / total );
	if( !isNaN( s ) ) {
		if( isFinite( s ) ) {
			target.setValue( s );
		}
	}
}

function decl_calcTargetTrendingFactor(r,factor_id,lic_id) {
	var lic_count = Number(r.getSource(lic_id).getValue());
	if( lic_count > 0 ) {
		var factor = Number(r.getSource(factor_id).getValue());
		var s = Number( factor * lic_count );
		target.setValue( s );
	}
	else {
		target.setValue( 0 );
	}
}

function decl_calcDecliningRiskIndicator(r) {
	var s = Number(0);
	for( i = 0; i < r.getSources().size(); i++ ) {
		var n = Number(r.getSources().get(i).getValue());
		if( !isNaN(n) ) {
			s += Number(n);
		}
	}
	target.setValue( Number( 1 - s ) );
}

function decl_avg(r) {
	var e = 0;
	for( i = 0; i < r.getSources().size(); i++ ) {
		var n = r.getSources().get(i).getValue();
		if( !isNaN(n) ) {
			if( isFinite(n) ) {
				e += n;
			}
		}
	}
	e = e / r.getSources().size();
	target.setValue(e);
}

function decl_calcNormalizedModernity(r,id1,id2) {
	var cur = Number(r.getSource(id1).getValue());
	var past = Number(r.getSource(id2).getValue());
	var ret = cur/past;
	if( !isNaN(ret) ) target.setValue( ret );
}

function decl_calcModernityFactor(r,id1,id2,id3) {
	var count = Number(r.getSource(id1).getValue());
	var factor = Number(r.getSource(id2).getValue());
	var total = Number(r.getSource(id3).getValue());
	var ret = count * ( factor / total );
	if( !isNaN(ret) ) {
		target.setValue( ret );
	}
}

function decl_calcObsoleteComponentsIndex(r) {
	var s = Number(0);
	for( i = 0; i < r.getSources().size(); i++ ) {
		var n = Number(r.getSources().get(i).getValue());
		if( !isNaN(n) ) {
			s += Number(n);
		}
	}
	target.setValue( Number(
			1 - s
	) );
}
